#!/usr/bin/env python
# -*- coding: utf-8 -*-
# __author__ = 'hongkefeng'


class TaskConfig(object):
    # 测试过程中有各种弹窗处理控制
    # 如果为 True，则在使用uihelper.find_element 函数过程中，会先检查是否有弹窗控件
    # 如果为 False，则在使用uihelper.find_element 函数过程中，不会检查弹框控件，
    IOSCheckPopupWindowBeforeFindElement = False
    AndroidCheckPopupWindowBeforeFindElement = False

    # 很多App启动的时候，会有各种欢迎页面
    # 如果为 True，则执行通用跳过脚本
    # 如果为 False，则不执行通用跳过脚本
    SkipFirstStartupWelcomePage = True

    # 为了避免长时间执行出现异常，可以配置每一个页面重启Appium Client
    # True, 页面遍历完成后，重启Appium client
    # False，页面遍历完成后，不重启Appium
    ReConnectingAppiumClient = False

    # Android 是否启用 UIAutomator2模式
    EnableAndroidUIAutomator2 = True

    # 保留字段
    MailNotification = False

    # 保留字段
    LogoutDetect = True

    # Android 弹框配置文件
    android_popup_elements_json = ""

    # iOS 弹框配置文件
    ios_popup_elements_json = ""

    # iOS 设备配置信息
    IOS_Device_Info = "http://uitest.daily.vdian.net/package/iOSDeviceInfo.json"

    # 自动截图位置
    Images_Dir = "./src/report/images"

    # Appium 查询元素等待时间
    Appium_Wait_Element_Time = 5

    # 框架支持的操作事件
    Support_Actions = [
        "url",
        "input",
        "click",
        "swipe",
        "exist",
        "not_exist",
        "iclick",
        "restart_app",
    ]

    # 默认日志存放路径
    LogPath = "../test.log"


class Platform(object):
    Android = "Android"
    IOS = "iOS"
