import os
from pyappium.log import SimpleLog


class AppiumParams(object):
    log = SimpleLog()
    APPIUM_PORT = os.getenv("APPIUMPORT")
    DEVICE_ID = os.getenv("DEVICEID")
    UIAUTOMATOR2PORT = os.getenv("UIAUTOMATOR2PORT")
    WDAPORT = os.getenv("WDAPORT")
    REPORT_DIR = os.path.join(os.path.abspath("."), "src", "report")

    if not DEVICE_ID:
        log.info("没有读取到读取环境变量DEVICE_ID，将使用默认值")

    if not APPIUM_PORT:
        log.info("没有读取到环境变量APPIUM_PORT， 使用默认值：4723")
        APPIUM_PORT = 4723

    if not UIAUTOMATOR2PORT:
        UIAUTOMATOR2PORT = 8201
        log.info("没有读取到环境变量UIAUTOMATOR2_PORT, 使用默认值")
